// frontend/tts-react-app/src/components/TextToSpeech.js
import React, { useState } from "react";
import axios from "axios";

const TextToSpeech = () => {
  const [text, setText] = useState("");
  const [audioUrl, setAudioUrl] = useState("");
  const [languageCode, setLanguageCode] = useState("en-US");
  const [voiceName, setVoiceName] = useState("en-US-Wavenet-D");
  const [gender, setGender] = useState("NEUTRAL");
  const [pitch, setPitch] = useState(0.0);
  const [speakingRate, setSpeakingRate] = useState(1.0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:5000/speak", {
        text,
        language_code: languageCode,
        voice_name: voiceName,
        gender,
        pitch,
        speaking_rate: speakingRate,
      });
      setAudioUrl(response.data.audio_file);
    } catch (error) {
      console.error("Error generating speech", error);
    }
  };

  return (
    <div className="container">
      <h1>Text-to-Speech</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          rows="4"
          cols="50"
        />
        <br />
        <label>
          Language Code:
          <input
            type="text"
            value={languageCode}
            onChange={(e) => setLanguageCode(e.target.value)}
          />
        </label>
        <br />
        <label>
          Voice Name:
          <input
            type="text"
            value={voiceName}
            onChange={(e) => setVoiceName(e.target.value)}
          />
        </label>
        <br />
        <label>
          Gender:
          <select value={gender} onChange={(e) => setGender(e.target.value)}>
            <option value="NEUTRAL">Neutral</option>
            <option value="MALE">Male</option>
            <option value="FEMALE">Female</option>
          </select>
        </label>
        <br />
        <label>
          Pitch:
          <input
            type="number"
            value={pitch}
            onChange={(e) => setPitch(parseFloat(e.target.value))}
            min="-20.0"
            max="20.0"
            step="0.1"
          />
        </label>
        <br />
        <label>
          Speaking Rate:
          <input
            type="number"
            value={speakingRate}
            onChange={(e) => setSpeakingRate(parseFloat(e.target.value))}
            min="0.25"
            max="4.0"
            step="0.1"
          />
        </label>
        <br />
        <button type="submit">Convert</button>
      </form>
      {audioUrl && (
        <audio controls>
          <source src={`http://127.0.0.1:5000/${audioUrl}`} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
      )}
    </div>
  );
};

export default TextToSpeech;
